    start = DEFAULT_START,
    end,
    decimalPlaces = getDefaultDecimalPlaces(start, end),
    formatter,
    decimalSeparator = '.',
    thousandsSeparator = '',
    prefix = '',
    suffix = '',
    shouldUseToLocaleString = false,
    toLocaleStringParams,
    fallbackPrefix = '',
    fallbackSuffix = '',
