import { TouchEvent } from 'react';
import DistanceAngleRecognizer from './DistanceAngleRecognizer';
import GestureController from '../controllers/GestureController';
import { UseGestureEvent, ReactEventHandlerKey, Fn } from '../types';
export default class PinchRecognizer extends DistanceAngleRecognizer {
    sharedStartState: {
        pinching: boolean;
    };
    sharedEndState: {
        pinching: boolean;
        down: boolean;
        touches: number;
    };
    constructor(controller: GestureController, args: any[]);
    getPayloadFromEvent(event: UseGestureEvent<TouchEvent>): {
        values: [number, number];
        gesturePayload: {
            origin: [number, number] | undefined;
        };
        sharedPayload: {
            touches: number;
            down: boolean;
            shiftKey: boolean;
            altKey: boolean;
            metaKey: boolean;
            ctrlKey: boolean;
        };
    };
    onPinchStart: (event: UseGestureEvent<TouchEvent<Element>>) => void;
    onPinchChange: (event: UseGestureEvent<TouchEvent<Element>>) => void;
    getEventBindings(): [ReactEventHandlerKey | ReactEventHandlerKey[], Fn][];
}
