import { WheelEvent } from 'react';
import CoordinatesRecognizer from './CoordinatesRecognizer';
import GestureController from '../controllers/GestureController';
import { UseGestureEvent, ReactEventHandlerKey, Fn } from '../types';
export default class WheelRecognizer extends CoordinatesRecognizer {
    sharedStartState: {
        wheeling: boolean;
    };
    sharedEndState: {
        wheeling: boolean;
        velocity: number;
        vxvy: number[];
    };
    continuousGesture: boolean;
    constructor(controller: GestureController, args: any[]);
    getPayloadFromEvent(event: UseGestureEvent<WheelEvent>): {
        values: [number, number];
        sharedPayload: {
            shiftKey: boolean;
            altKey: boolean;
            metaKey: boolean;
            ctrlKey: boolean;
        };
    };
    onWheel: (event: UseGestureEvent<WheelEvent<Element>>) => void;
    getEventBindings(): [ReactEventHandlerKey | ReactEventHandlerKey[], Fn][];
}
